import React from 'react';
import { Line } from 'react-chartjs-2';

const TemperatureChart = ({ currentTemperature, forecastedTemperatures }) => {
  const labels = ['Now', ...forecastedTemperatures.map((_, index) => `+${index + 1}h`)];
  
  const chartData = {
    labels: labels,
    datasets: [
      {
        label: 'Temperature',
        data: [currentTemperature, ...forecastedTemperatures],
        borderColor: 'rgba(75, 192, 192, 1)',
        fill: true,
      },
    ],
  };

  

  return (
    <div>
      <Line data={chartData} />
    </div>
  );
};

export default TemperatureChart;
