import React from 'react';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto'

const formatHourlyTime = (timestamp) => {
    const options = { hour: 'numeric', hour12: true };
    return new Intl.DateTimeFormat('en-US', options).format(new Date(timestamp * 1000));
};

const TempChart = ({ currentTemperature, forecastedTemperatures, forecastedTimes }) => {
    console.log('Current Temperature:', currentTemperature);
    console.log('Forecasted Temperatures:', forecastedTemperatures);
    const labels = ['Now', ...forecastedTimes.map(time => formatHourlyTime(time))];
    
    const chartData = {
        labels: labels,
        datasets: [
        {
            label: 'Temperature',
            data: [currentTemperature, ...forecastedTemperatures],
            borderColor: 'rgba(75, 192, 192, 1)',
            fill: true,
        },
        ],
    };

    

    return (
        <div>
        <Line data={chartData} />
        </div>
    );
};

export default TempChart;
