import React, { useState } from 'react';
import './App.css';

function App() {
    const [city, setCity] = useState('');
    const [country, setCountry] = useState('');
    const [weatherData, setWeatherData] = useState(null);

    const apiKey = 'YOUR_API_KEY';
    const weatherApiUrl = 'https://api.openweathermap.org/data/2.5/weather';
    const locationApiUrl = 'https://your-location-api-url'; // Replace with the actual location API URL

    const countries = [
        { code: 'US', name: 'United States' },
        { code: 'CA', name: 'Canada' },
        // Add more countries as needed
    ];

    const getWeatherData = async () => {
        try {
            const locationResponse = await fetch(`${locationApiUrl}?city=${city}&state=${'stateCode'}&country=${country}`);
            const locationData = await locationResponse.json();

            const weatherResponse = await fetch(`${weatherApiUrl}?lat=${locationData.lat}&lon=${locationData.lon}&appid=${apiKey}`);
            const weatherData = await weatherResponse.json();

            setWeatherData(weatherData);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        getWeatherData();
    };

    return (
        <div className="App">
            <form onSubmit={handleSubmit}>
                <label>
                    Enter city name:
                    <input type="text" value={city} onChange={(e) => setCity(e.target.value)} />
                </label>
                <label>
                    Select country:
                    <select value={country} onChange={(e) => setCountry(e.target.value)}>
                        {countries.map((country) => (
                            <option key={country.code} value={country.code}>
                                {country.name}
                            </option>
                        ))}
                    </select>
                </label>
                <button type="submit">Get Weather</button>
            </form>

            {weatherData && (
                <div>
                    <h2>{weatherData.name}</h2>
                    <p>{Math.round(weatherData.main.temp - 273.15)}Â°C</p>
                    <p>{weatherData.weather[0].description}</p>
                </div>
            )}
        </div>
    );
}

export default App;
