import React, { useState } from 'react';
import './App.css';

function App() {
    const [city, setCity] = useState('');
    const [country, setCountry] = useState('');
    const stateCode = "NY";
    const [weatherData, setWeatherData] = useState(null);
    const [locationData, setLocationData] = useState(null);


    const apiKey = '1a67ab44359d61742560aa00d59baf0b';
    const locationApiUrl = `http://api.openweathermap.org/geo/1.0/direct?q=${city}&limit=1&appid=${apiKey}`;

    const countries = [
        { code: 'US', name: 'United States' },
        { code: 'CA', name: 'Canada' },
        // Add more countries as needed
    ];

    const getWeatherData = async () => {
        try {
            const locationResponse = await fetch(locationApiUrl);
            const locationData = await locationResponse.json();

            setLocationData(locationData);

            const weatherApiUrl = `https://api.openweathermap.org/data/3.0/onecall?lat=${locationData && locationData.length > 0 ? locationData[0].lat : ''}&lon=${locationData && locationData.length > 0 ? locationData[0].lon : ''}&appid=${apiKey}`;

            const weatherResponse = await fetch(weatherApiUrl);
            const weatherData = await weatherResponse.json();

            setWeatherData(weatherData);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        getWeatherData();
    };

    const kelvinToCelsius = (kelvin) => kelvin - 273.15;

    return (
        <div className="App">
            <form onSubmit={handleSubmit}>
                <label>
                    Enter city name:
                    <input type="text" value={city} onChange={(e) => setCity(e.target.value)} />
                </label>
                <label>
                    Select country:
                    <select value={country} onChange={(e) => setCountry(e.target.value)}>
                        {countries.map((country) => (
                            <option key={country.code} value={country.code}>
                                {country.name}
                            </option>
                        ))}
                    </select>
                </label>
                <button type="submit">Get Weather</button>
            </form>

            {weatherData && weatherData.current && (
                <div>
                    <h2>Current Weather</h2>
                    <p>Temperature: {kelvinToCelsius(weatherData.current.temp).toFixed(2)} °C</p>
                    <p>Feels Like: {kelvinToCelsius(weatherData.current.feels_like).toFixed(2)} °C</p>
                    <p>Humidity: {weatherData.current.humidity ? `${weatherData.current.humidity}%` : 'N/A'}</p>
                    <p>Weather: {weatherData.current.weather && weatherData.current.weather[0] ? weatherData.current.weather[0].description : 'N/A'}</p>
                </div>
            )}

            {locationData && (
                <div>
                    {/* Display location data here */}
                    <h2>Location Details</h2>
                    <p>Latitude: {locationData[0].lat}</p>
                    <p>Longitude: {locationData[0].lon}</p>
                    {/* Add more details as needed */}
                </div>
            )}
        </div>
    );
}

export default App;
