import React from 'react';
import { Line } from 'react-chartjs-2';
import { LinearScale, CategoryScale, Chart } from "chart.js";

Chart.register(CategoryScale);
Chart.register(LinearScale);

const TempChart = ({ currentTemperature, forecastedTemperatures }) => {
    console.log('Current Temperature:', currentTemperature);
    console.log('Forecasted Temperatures:', forecastedTemperatures);
    const labels = ['Now', ...forecastedTemperatures.map((_, index) => `+${index + 1}h`)];
    
    const chartData = {
        labels: labels,
        datasets: [
        {
            label: 'Temperature',
            data: [currentTemperature, ...forecastedTemperatures],
            borderColor: 'rgba(75, 192, 192, 1)',
            fill: true,
        },
        ],
    };

    

    return (
        <div>
        <Line data={chartData} />
        </div>
    );
};

export default TempChart;
