import React, { useState } from 'react';
import './App.css';

function App() {
    const [city, setCity] = useState('');
    const [country, setCountry] = useState('');
    const [stateCode, setStateCode] = useState('');
    // const stateCode = "NY";
    const [weatherData, setWeatherData] = useState(null);
    const [locationData, setLocationData] = useState(null);
    const [errorMessage, setErrorMessage] = useState('');


    const apiKey = '1a67ab44359d61742560aa00d59baf0b';
    // const locationApiUrl = `http://api.openweathermap.org/geo/1.0/direct?q=${city}&limit=1&appid=${apiKey}`;

    const countries = [
        { code: 'CA', name: 'Canada' },
        { code: 'US', name: 'United States' }
        // Add more countries as needed
    ];

    const usStates = [
        { code: 'AL', name: 'Alabama' },
        { code: 'AK', name: 'Alaska' },
        { code: 'AZ', name: 'Arizona' },
        { code: 'AR', name: 'Arkansas' },
        { code: 'CA', name: 'California' },
        { code: 'CO', name: 'Colorado' },
        { code: 'CT', name: 'Connecticut' },
        { code: 'DE', name: 'Delaware' },
        { code: 'FL', name: 'Florida' },
        { code: 'GA', name: 'Georgia' },
        { code: 'HI', name: 'Hawaii' },
        { code: 'ID', name: 'Idaho' },
        { code: 'IL', name: 'Illinois' },
        { code: 'IN', name: 'Indiana' },
        { code: 'IA', name: 'Iowa' },
        { code: 'KS', name: 'Kansas' },
        { code: 'KY', name: 'Kentucky' },
        { code: 'LA', name: 'Louisiana' },
        { code: 'ME', name: 'Maine' },
        { code: 'MD', name: 'Maryland' },
        { code: 'MA', name: 'Massachusetts' },
        { code: 'MI', name: 'Michigan' },
        { code: 'MN', name: 'Minnesota' },
        { code: 'MS', name: 'Mississippi' },
        { code: 'MO', name: 'Missouri' },
        { code: 'MT', name: 'Montana' },
        { code: 'NE', name: 'Nebraska' },
        { code: 'NV', name: 'Nevada' },
        { code: 'NH', name: 'New Hampshire' },
        { code: 'NJ', name: 'New Jersey' },
        { code: 'NM', name: 'New Mexico' },
        { code: 'NY', name: 'New York' },
        { code: 'NC', name: 'North Carolina' },
        { code: 'ND', name: 'North Dakota' },
        { code: 'OH', name: 'Ohio' },
        { code: 'OK', name: 'Oklahoma' },
        { code: 'OR', name: 'Oregon' },
        { code: 'PA', name: 'Pennsylvania' },
        { code: 'RI', name: 'Rhode Island' },
        { code: 'SC', name: 'South Carolina' },
        { code: 'SD', name: 'South Dakota' },
        { code: 'TN', name: 'Tennessee' },
        { code: 'TX', name: 'Texas' },
        { code: 'UT', name: 'Utah' },
        { code: 'VT', name: 'Vermont' },
        { code: 'VA', name: 'Virginia' },
        { code: 'WA', name: 'Washington' },
        { code: 'WV', name: 'West Virginia' },
        { code: 'WI', name: 'Wisconsin' },
        { code: 'WY', name: 'Wyoming' },
    ];

    const getWeatherData = async () => {
        try {
            const locationApiUrl = country === 'US'
                ? `http://api.openweathermap.org/geo/1.0/direct?q=${city},${stateCode},${country}&limit=1&appid=${apiKey}`
                : `http://api.openweathermap.org/geo/1.0/direct?q=${city},${country}&limit=1&appid=${apiKey}`;
            const locationResponse = await fetch(locationApiUrl);
            const locationData = await locationResponse.json();

            if (!locationData || locationData.length === 0) {
                setErrorMessage('Location not found. Please check the entered place and try again.');
                return;
            }

            setLocationData(locationData);

            const weatherApiUrl = `https://api.openweathermap.org/data/3.0/onecall?lat=${locationData && locationData.length > 0 ? locationData[0].lat : ''}&lon=${locationData && locationData.length > 0 ? locationData[0].lon : ''}&appid=${apiKey}`;

            const weatherResponse = await fetch(weatherApiUrl);
            const weatherData = await weatherResponse.json();

            setWeatherData(weatherData);
            setErrorMessage('');
        } catch (error) {
            console.error('Error fetching data:', error);

            // Check if the error is due to a specific condition (e.g., place not found)
            if (error.message.includes('undefined')) {
                // Handle the specific error condition
                alert('Location not found. Please check the entered place and try again.');
            } else {
                // For other types of errors, provide a generic error message
                alert('An error occurred while fetching location data. Please try again later.');
            }
        }
    };

    const handleCountryChange = (e) => {
        const selectedCountry = e.target.value;
        setCountry(selectedCountry);

        // If the selected country is the United States, set an empty state code
        if (selectedCountry === 'US') {
            setStateCode('');
        }
    };

    const handleStateChange = (e) => {
        const selectedStateCode = e.target.value;
        setStateCode(selectedStateCode);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        getWeatherData();
    };

    const kelvinToCelsius = (kelvin) => kelvin - 273.15;

    return (
        <div className="App">
            <form onSubmit={handleSubmit}>
                <label>
                    Enter city name:
                    <input type="text" value={city} onChange={(e) => setCity(e.target.value)} />
                </label>
                <label>
                    Select country:
                    <select value={country} onChange={handleCountryChange}>
                        {countries.map((country) => (
                            <option key={country.code} value={country.code}>
                                {country.name}
                            </option>
                        ))}
                    </select>
                </label>

                {country === 'US' && (
                    <label>
                        Select state:
                        <select value={stateCode} onChange={handleStateChange}>
                            {usStates.map((state) => (
                                <option key={state.code} value={state.code}>
                                    {state.name}
                                </option>
                            ))}
                        </select>
                    </label>
                )}          

                <button type="submit">Get Weather</button>
            </form>

            {errorMessage && (
                <div>
                    <p style={{ color: 'red' }}>{errorMessage}</p>
                </div>
            )}

            {weatherData && weatherData.current && (
                <div>
                    <h2>Current Weather</h2>
                    <p>Temperature: {kelvinToCelsius(weatherData.current.temp).toFixed(2)} °C</p>
                    <p>Feels Like: {kelvinToCelsius(weatherData.current.feels_like).toFixed(2)} °C</p>
                    <p>Humidity: {weatherData.current.humidity ? `${weatherData.current.humidity}%` : 'N/A'}</p>
                    <p>Weather: {weatherData.current.weather && weatherData.current.weather[0] ? weatherData.current.weather[0].description : 'N/A'}</p>
                </div>
            )}

            {locationData && locationData.length > 0 && (
                <div>
                    {/* Display location data here */}
                    <h2>Location Details</h2>
                    <p>Latitude: {locationData[0].lat}</p>
                    <p>Longitude: {locationData[0].lon}</p>
                    {/* Add more details as needed */}
                </div>
            )}
        </div>
    );
}

export default App;
